//  DetailedBuildPlanView.swift
//  DIYGenieApp
//  Displays a full step-by-step DIY plan generated by AI.
//  Updated: 2025-11-04
//

import SwiftUI

struct DetailedBuildPlanView: View {
    let plan: PlanResponse?
    let completedSteps: [Int]

    init(plan: PlanResponse?, completedSteps: [Int] = []) {
        self.plan = plan
        self.completedSteps = completedSteps
    }

    var body: some View {
        ZStack {
            LinearGradient(
                gradient: Gradient(colors: [Color("BGStart"), Color("BGEnd")]),
                startPoint: .topLeading,
                endPoint: .bottomTrailing
            )
            .ignoresSafeArea()

            if let plan = plan {
                ScrollView {
                    VStack(alignment: .leading, spacing: 22) {
                        // MARK: - Header
                        HStack {
                            Image(systemName: "wrench.and.screwdriver")
                                .foregroundColor(.white.opacity(0.9))
                            Spacer()
                        }

                        // MARK: - Title & Overview
                        Text("Detailed Build Plan")
                            .font(.system(size: 30, weight: .bold))
                            .foregroundColor(.white)

                        if let overview = plan.summary, !overview.isEmpty {
                            Text(overview)
                                .font(.subheadline)
                                .foregroundColor(.white.opacity(0.75))
                        }

                        // MARK: - Materials  (non-optional array)
                        let materials = plan.materials
                        if !materials.isEmpty {
                            SectionHeader("Materials")
                            VStack(alignment: .leading, spacing: 8) {
                                ForEach(materials, id: \.self) { item in
                                    HStack(alignment: .top, spacing: 8) {
                                        Circle()
                                            .frame(width: 6, height: 6)
                                            .foregroundColor(.white.opacity(0.65))
                                            .padding(.top, 7)
                                        Text(item)
                                            .foregroundColor(.white.opacity(0.9))
                                            .font(.body)
                                    }
                                }
                            }
                            .padding(14)
                            .background(Color.white.opacity(0.08))
                            .cornerRadius(12)
                        }

                        // MARK: - Tools  (non-optional array)
                        let tools = plan.tools
                        if !tools.isEmpty {
                            SectionHeader("Tools")
                            VStack(alignment: .leading, spacing: 8) {
                                ForEach(tools, id: \.self) { tool in
                                    HStack(alignment: .top, spacing: 8) {
                                        RoundedRectangle(cornerRadius: 2)
                                            .frame(width: 6, height: 6)
                                            .foregroundColor(.white.opacity(0.65))
                                            .padding(.top, 7)
                                        Text(tool)
                                            .foregroundColor(.white.opacity(0.9))
                                            .font(.body)
                                    }
                                }
                            }
                            .padding(14)
                            .background(Color.white.opacity(0.08))
                            .cornerRadius(12)
                        }

                        // MARK: - Cost
                        if let cost = plan.estimatedCost {
                            SectionHeader("Estimated Cost")
                            Text("$\(Int(cost))")
                                .foregroundColor(.white)
                                .font(.headline)
                                .padding(.horizontal, 14)
                                .padding(.vertical, 10)
                                .background(Color.white.opacity(0.08))
                                .cornerRadius(12)
                        }

                        // MARK: - Progress & Steps (steps is NON-optional)
                        let steps = plan.steps
                        let total = steps.count
                        let doneSet = Set(completedSteps)
                        let progress = Double(doneSet.count) / Double(max(total, 1))

                        VStack(alignment: .leading, spacing: 8) {
                            SectionHeader("Progress")
                            ProgressView(value: progress)
                                .tint(Color.purple)
                            Text("\(doneSet.count) of \(total) steps complete")
                                .font(.caption)
                                .foregroundColor(.white.opacity(0.7))
                        }

                        if !steps.isEmpty {
                            SectionHeader("Steps")
                            VStack(alignment: .leading, spacing: 12) {
                                ForEach(Array(steps.enumerated()), id: \.0) { (idx, text) in
                                    StepCard(
                                        number: idx + 1,
                                        text: text,
                                        isComplete: completedSteps.contains(idx)
                                    )
                                }
                            }
                            .padding(.top, 4)
                        } else {
                            Text("No steps available.")
                                .foregroundColor(.white.opacity(0.7))
                                .italic()
                        }

                        Spacer(minLength: 32)
                    }
                    .padding(.horizontal, 20)
                    .padding(.top, 20)
                }
            } else {
                VStack(spacing: 12) {
                    ProgressView()
                        .progressViewStyle(CircularProgressViewStyle(tint: .white))
                    Text("Loading plan...")
                        .foregroundColor(.white.opacity(0.85))
                        .font(.system(size: 16))
                }
            }
        }
        .preferredColorScheme(.dark)
    }
}

private struct SectionHeader: View {
    let title: String
    init(_ title: String) { self.title = title }
    var body: some View {
        Text(title)
            .font(.headline.weight(.semibold))
            .foregroundColor(.white)
            .padding(.top, 4)
    }
}

private struct StepCard: View {
    let number: Int
    let text: String
    let isComplete: Bool

    var body: some View {
        HStack(alignment: .top, spacing: 12) {
            Image(systemName: isComplete ? "checkmark.circle.fill" : "circle")
                .foregroundColor(isComplete ? Color.green : Color.white.opacity(0.6))
                .font(.system(size: 18, weight: .semibold))

            VStack(alignment: .leading, spacing: 6) {
                Text("Step \(number)")
                    .font(.subheadline.weight(.semibold))
                    .foregroundColor(.white)
                Text(text)
                    .font(.body)
                    .foregroundColor(.white.opacity(0.9))
            }
            Spacer(minLength: 0)
        }
        .padding(14)
        .background(Color.white.opacity(0.08))
        .cornerRadius(12)
    }
}
